typedef enum {FALSE, TRUE} bool;        //bolean simple struct for avoiding other values

void print(bool grid[L][L]);            //print every second the grid
void start(bool grid[L][L]);            //random grid filling
void rend(bool grid[L][L]);             //load next "frame"
int count(bool a[L][L], int x, int y);  //count the num of live neighbors
void example1(bool grid[17][17]);
void example2(bool grid[35][35]);
void example3(bool grid[38][38]);
void example4(bool grid[5][5]);
void example5(bool grid[10][10]);

int main(int argc, char *argv[]){
    srand(time(0));
    bool grid[L][L];
    start(grid);
    //example1(grid);
    //example2(grid);
    //example3(grid);
    //example4(grid);
    //example5(grid);
    print(grid);
    return 0;}

void print(bool grid[L][L]){
    rend(grid);
    while(1){
        sleep(1);
        printf("\e[1;1H\e[2J");
        rend(grid);
        for(int i=0;i<L;i++){
            for(int j=0;j<L;j++) (grid[i][j]) ? printf(" * ") : printf("   ");
            printf("\n");}}}

void start(bool grid[L][L]){for(int i=0;i<L;i++) for(int j=0;j<L;j++) grid[i][j]=rand()%2;}

void rend(bool grid[L][L]){
    int c;
    bool grid2[L][L];
    for(int i=0;i<L;i++) for(int j=0;j<L;j++){
        c = count(grid,i,j);
        if(grid[i][j]) grid2[i][j] = (c==2 || c==3) ? 1 : 0;
        else grid2[i][j] = (c==3) ? 1 : 0;}
    for(int i=0;i<L;i++) for(int j=0;j<L;j++) grid[i][j]=grid2[i][j];}

int count(bool a[L][L], int x, int y){
    int ux=x-1, dx=x+1, ly=y-1, ry=y+1;
    if(x==0){ux=L-1; dx=1;}
    if(x==L-1){ux=L-2; dx=0;}
    if(y==0){ly=L-1; ry=1;}
    if(y==L-1){ly=L-2; ry=0;}
    return a[ux][ly]+a[x][ly]+a[dx][ly]+a[ux][y]+a[dx][y]+a[ux][ry]+a[x][ry]+a[dx][ry];}

